import React, { Component, useState, useEffect } from 'react'
import {
    BrowserRouter as Router,
    Switch,
    Route,
    Link
} from "react-router-dom";
import '../TopNav/TopNav.css'
import hamburger from "../Resources/PinClipart.com_cempasuchil-clipart_1812493.png"

import Nav from 'react-bootstrap/Nav'
import Navbar from 'react-bootstrap/Navbar'
import Image from 'react-bootstrap/Image'




export default function TopNav() {

    const [buttonvisibility, setbuttonvisibility] = useState("hidden")


    const showButton = () => {
        if (window.innerWidth < 960) {
            setbuttonvisibility('visible')
        } else {
            setbuttonvisibility('hidden')
        }
    }

    useEffect(() => {
        showButton();
    });
    window.addEventListener('resize', showButton);

    function tableNormal() {
        return (
            <table>
                <tbody>
                    <tr>
                        <td className="brand"><h1>Kanta Husari</h1></td>
                        <td className="links"><h1>Links</h1></td>
                        <td className="links"><h1>Links</h1></td>
                        <td className="links"><h1>Links</h1></td>
                        <td className="links"><h1>Links</h1></td>
                        <td className="links"><h1>Links</h1></td>
                        <td className="links"><h1>Links</h1></td>
                    </tr>
                </tbody>
            </table>
        )
    }

    function tablesmall() {
        return (
            <table>
                <tbody>
                    <tr>
                        <td className="brand"><h1>Kanta Husari</h1></td>
                        <td>
                            <div className="image" style={{ visibility: `${buttonvisibility}` }}>
                                <Image src={hamburger} className='collapseButton' onClick={(event) => { burger(event) }} />
                            </div>
                        </td>
                    </tr>
                </tbody>
            </table>
        )
    }

    function burger(event) {
        event.preventDefault();
        console.log('clicked')
    }

    //rendering here
    if (window.innerWidth < 960) {
        return (
            < div className="top" >
                {tablesmall()}
            </div >
        )
    } else {
        return (
            < div className="top" >
                {tableNormal()}
            </div >
        )
    }
}
