import React, { Component, useState, useEffect } from 'react'
import "./Section02.css"

export default function Section02() {
    const [marginLeft, setmarginLeft] = useState(0)
    const [fontSize, setfontSize] = useState(null)

    const [Pystatus, setPystatus] = useState(false)

    const [Pytext, setPytext] = useState("")
    const [Pandastext, setPandastext] = useState("")
    const [Numpytext, setNumpytext] = useState("")
    const [Djangotext, setDjangotext] = useState("")
    const [Tkintertext, setTkintertext] = useState("")

    const [Pymargin, setPymargin] = useState(45)
    const [Pandasmargin, setPandasmargin] = useState(0)
    const [Numpymargin, setNumpymargin] = useState(0)
    const [Djangomargin, setDjangomargin] = useState(0)
    const [Tkintermargin, setTkintermargin] = useState(0)

    function animatePy() {
        setPystatus(!Pystatus)
        if (Pystatus) {
            setPymargin(20)
        } else {
            setPymargin(45)
        }
    }

    function setFontsize() {
        setmarginLeft(window.innerWidth / 5)
        if (window.innerWidth < 960) {
            setfontSize(`${window.innerWidth * (3 / 100)}px`)
        } else {
            setfontSize(`${window.innerWidth * (4 / 100)}px`)
        }
    }
    useEffect(() => {
        setFontsize();
    });
    window.addEventListener('resize', setFontsize);
    return (
        <div className="containerpy">
            <div className="Pandas" style={{ fontSize: `${fontSize}` }}>Pandas</div>
            <div className="Numpy" style={{ fontSize: `${fontSize}` }}>Numpy</div>
            <div className="Django" style={{ fontSize: `${fontSize}` }}>Django</div>
            <div className="Tkinter" style={{ fontSize: `${fontSize}` }}>Tkinter</div>
            {/* <div className="right" style={{ fontSize: `${fontSize}` }}><h1>Python</h1></div> */}
            <div className="Python" onClick={() => { animatePy() }} style={{ marginRight: `${Pymargin}%`, fontSize: `${fontSize}` }}>Python</div>
        </div>
    )
}


