/* eslint-disable jsx-a11y/alt-text */
import React, { useState, useEffect } from 'react'
import "./Projects.css"

import leftArrow from "../../Resources/left-arrow.png"
import rightArrow from "../../Resources/right-arrow.png"

export default function Projects() {

    const [width, setwidth] = useState(window.innerWidth * (10 / 100))
    function changeDimention() {
        setwidth(window.innerWidth * (10 / 100))
    }
    useEffect(() => {
        changeDimention();
    });
    window.addEventListener('resize', changeDimention)
    return (
        <div className="Pgalery">

            <div>
                <h1>Projects</h1>
            </div>

            <table className="ProjectShowArea">
                <tbody className="ProjectShowArea">
                    <tr>
                        <td className="leftarr"><div ><img className="leftarrimage" src={leftArrow} style={{ width: width, height: width }}></img></div></td>
                        <td className="midshow"><div>Show</div></td>
                        <td className="rightarr"><div ><img className="rightarrimage" src={rightArrow} style={{ width: width, height: width }}></img></div></td>
                    </tr>
                </tbody>
            </table>
        </div>
    )
}
