import React, { useState, useEffect } from 'react'
import "./ContactMe.css"

export default function ContactMe() {

    const [name, setname] = useState("")
    const [emailaddress, setemailaddress] = useState("")
    const [phone, setphone] = useState("")
    const [subject, setsubject] = useState("")
    const [message, setmessage] = useState("")


    const [nameerror, setnameerror] = useState("2px solid rgba(0, 128, 0, 0)")
    const [emailerror, setemailerror] = useState("2px solid rgba(0, 128, 0, 0)")
    const [phoneerror, setphoneerror] = useState("2px solid rgba(0, 128, 0, 0)")
    const [subjecterror, setsubjecterror] = useState("2px solid rgba(0, 128, 0, 0)")
    const [messageerror, setmessageerror] = useState("2px solid rgba(0, 128, 0, 0)")



    const [errorMessage, seterrorMessage] = useState("")

    function submitForm() {
        //fields empty?
        //yes .. process empty
        if (name === "") {
            seterrorMessage("One Or more field is empty")
            setnameerror("2px solid red")
        } else {
            seterrorMessage("")
            setnameerror("2px solid rgba(0, 128, 0, 0)")
        }

        if (emailaddress === "") {
            seterrorMessage("One Or more field is empty")
            setemailerror("2px solid red")
        } else {
            seterrorMessage("")
            setemailerror("2px solid rgba(0, 128, 0, 0)")
        }

        if (phone === "") {
            seterrorMessage("One Or more field is empty")
            setphoneerror("2px solid red")
        } else {
            seterrorMessage("")
            setphoneerror("2px solid rgba(0, 128, 0, 0)")
        }

        if (subject === "") {
            seterrorMessage("One Or more field is empty")
            setsubjecterror("2px solid red")
        } else {
            seterrorMessage("")
            setsubjecterror("2px solid rgba(0, 128, 0, 0)")
        }

        if (message === "") {
            seterrorMessage("One Or more field is empty")
            setmessageerror("2px solid red")
        } else {
            seterrorMessage("")
            setmessageerror("2px solid rgba(0, 128, 0, 0)")
        }
        //no .. process post
        if(name !==""||emailaddress !==""||phone !==""||subject !==""||message !==""){
            seterrorMessage("")
        }

    }

    return (
        <div className="ContactMeCont">
            <div>
                <h1>Contact Me</h1>
            </div>
            <input className="inputField" placeholder="Name" onChange={(event) => { event.preventDefault(); setname(event.target.value) }} style={{ border: nameerror }}></input><br />
            <input className="inputField" placeholder="Email" onChange={(event) => { event.preventDefault(); setemailaddress(event.target.value) }} style={{ border: emailerror }}></input><br />
            <input className="inputField" placeholder="Phone Number" onChange={(event) => { event.preventDefault(); setphone(event.target.value) }} style={{ border: phoneerror }}></input><br />
            <input className="inputField" placeholder="Subject" onChange={(event) => { event.preventDefault(); setsubject(event.target.value) }} style={{ border: subjecterror }}></input><br />
            <textarea className="textfield" placeholder="Message" onChange={(event) => { event.preventDefault(); setmessage(event.target.value) }} style={{ border: messageerror }}></textarea ><br />
            <button className="submit" onClick={() => submitForm()}>Send</button><br />
            <div>
                <h1 style={{ color: "red" }}>{errorMessage}</h1>
            </div>
        </div>
    )
}
