import React, { Component, useState, useEffect } from 'react'
import {
    BrowserRouter as Router,
    Switch,
    Route,
    Link
} from "react-router-dom";
import '../TopNav/TopNav.css'
import hamburger from "../Resources/PinClipart.com_cempasuchil-clipart_1812493.png"

import Nav from 'react-bootstrap/Nav'
import Navbar from 'react-bootstrap/Navbar'
import Image from 'react-bootstrap/Image'




export default function TopNav() {

    const [button, setbutton] = useState(false)

    const showButton = () => {
        if (window.innerWidth < 960) {
            setbutton(false)
            console.log(button)
            console.log(window.innerWidth)
        } else {
            setbutton(true)
            console.log(button)
            
        }
    }

    useEffect(() => {
        showButton();
    }, []);

    function burger(event) {
        event.preventDefault();
        // window.alert('Hello World')

        console.log(window.innerWidth)
    }
    if (window.size <= 966) {
        document.getElementsByClassName('image').style = { marginLeft: "85%" }
    } else {
        document.getElementsByClassName('image').style = { marginLeft: "85%" }
    }
    return (
        <div className="top">
            <div className="brand">
                Kanta Husari
            </div>
            <div className="image" >
                <Image src={hamburger} className='collapseButton' onClick={(event) => { burger(event) }} />
            </div>


        </div>
    )
}



