import React, { Component, useState } from 'react'
import "../Body/Body.css"



export default function Body() {

    const [jsStatus, setjsStatus] = useState(false)

    const [js, setjs] = useState('0px')
    const [js1, setjs1] = useState('0px')
    const [js2, setjs2] = useState('0px')
    const [js3, setjs3] = useState('0px')
    const [js4, setjs4] = useState('0px')

    function animate() {
        setjsStatus(!jsStatus)
        if (jsStatus) {
            //if false and on page load
            //animation
            setjs('0px')
            setjs1('0px')
            setjs2('0px')
            setjs3('0px')
            setjs4('0px')
        } else {
            //if true and on change
            //animation
            setjs('250px')
            
        }
    }
    return (
        // <div className="container">
        //     <div className="section01">
        //         <div className='js' onClick={() => { animate() }} style={{ marginLeft: `${jsmargin}` }}>
        //             {/* <h2>{`${jsText}`}</h2> */}
        //             <span className='js1'><h2>{`${jsText}`}</h2></span>
        //         </div>
        //         <div className='js1' style={{ marginLeft: `${js1margin}` }}>
        //             <h2>{`${js1Text}`}</h2>
        //         </div>

        //         <div className='js2' style={{ marginLeft: `${js1margin}` }}>
        //             <h2>{`${js1Text}`}</h2>
        //         </div>
        //     </div>
        //     <div className="section02">this is body</div>
        //     <div className="section03">this is body</div>
        // </div >

        <div className="container">
            <div className="js4" onClick={() => { animate() }} style={{ backgroundColor: "white" }}>
                white
            </div>
            <div className="js3" style={{ backgroundColor: "red" }}>
                red
            </div>
            <div className="js2" style={{ backgroundColor: "green" }}>
                green
            </div>
            <div className="js1" style={{ backgroundColor: "blue" }}>
                blue
            </div>
            <div className="js" onClick={() => { animate() }} style={{ backgroundColor: "purple", marginRight: `${js}` }}>
                purpule
            </div>
            {/* <div className="inner hide" style={{ backgroundcolor: "black" }}></div> */}
        </div>
    )
}
