/* eslint-disable jsx-a11y/alt-text */
import React, { useState, useEffect } from 'react'
import "./Skills.css"

import design from "../../Resources/Design.png"
import coding from "../../Resources/data.png"
import frameworks from "../../Resources/framework.png"
import databases from "../../Resources/server.png"
import devops from "../../Resources/DevOps.png"

export default function Skills() {
    const skills = {
        html: {
            name: "HTML",
            percentage: "90%",
        },
        css: {
            name: "CSS",
            percentage: "70%",
        },
        sass: {
            name: "Sass",
            percentage: "50%",
        },
        Bootstrap: {
            name: "Bootstrap",
            percentage: "90%",
        },
        Photoshop: {
            name: "Photoshop",
            percentage: "90%",
        },


        JavaScript: {
            name: "JavaScript",
            percentage: "75%",
        },
        PHP: {
            name: "PHP",
            percentage: "60%",
        },
        Python: {
            name: "Python",
            percentage: "70%",
        },
        CS: {
            name: "C#",
            percentage: "50%",
        },
        Java: {
            name: "Java",
            percentage: "50%",
        },
        MySQL: {
            name: "MySQL",
            percentage: "80%",
        },
        PostgreSQL: {
            name: "PostgreSQL",
            percentage: "50%",
        },
        SQLServer: {
            name: "SQL Server",
            percentage: "45%",
        },
        SQLite: {
            name: "SQLite",
            percentage: "70%",
        },
        MongoDB: {
            name: "MongoDB",
            percentage: "70%",
        },
        GraphQL: {
            name: "GraphQL",
            percentage: "60%",
        },
        NodeJS: {
            name: "Node.JS",
            percentage: "70%",
        },
        Express: {
            name: "Express",
            percentage: "60%",
        },
        Mern: {
            name: "Mern",
            percentage: "75%",
        },
        Mean: {
            name: "Mean",
            percentage: "60%",
        },
        Django: {
            name: "Django",
            percentage: "50%",
        },
        Tkinter: {
            name: "Tkinter",
            percentage: "50%",
        },
        GitHub: {
            name: "GitHub",
            percentage: "60%",
        },
        Docker: {
            name: "Docker",
            percentage: "50%",
        },
        TravisCI: {
            name: "TravisCI",
            percentage: "60%",
        },
        AWS: {
            name: "AWS",
            percentage: "45%",
        },
        DigitalOcean: {
            name: "DigitalOcean",
            percentage: "45%",
        },
    }
    const [width, setwidth] = useState(window.innerWidth * (10 / 100))
    function changeDimention() {
        setwidth(window.innerWidth * (7 / 100))
    }
    useEffect(() => {
        changeDimention();
    });
    window.addEventListener('resize', changeDimention)
    return (
        <div className="container">
                        <div className="Skills">
                <h1>Skills</h1>
            </div>
            <table style={{ marginTop:"15px",backgroundColor:"#ffffff00" }}>
                <tbody>
                    <tr>
                        <th>
                            <div className="catimage" style={{ width: width, height: width }}><img className="caticon" src={design} style={{ width: width, height: width }}></img></div>
                        </th>
                        <th>
                            <div className="catimage" style={{ width: width, height: width }}><img className="caticon" src={coding} style={{ width: width, height: width }}></img></div>
                        </th>
                        <th>
                            <div className="catimage" style={{ width: width, height: width }}><img className="caticon" src={frameworks} style={{ width: width, height: width }}></img></div>
                        </th>
                        <th>
                            <div className="catimage" style={{ width: width, height: width }}><img className="caticon" src={databases} style={{ width: width, height: width }}></img></div>
                        </th>
                        <th>
                            <div className="catimage" style={{ width: width, height: width }}><img className="caticon" src={devops} style={{ width: width, height: width }}></img></div>
                        </th>
                    </tr>
                </tbody>
            </table>



        </div>
    )
}
