import React, { Component, useState } from 'react'
import "../Body/Body.css"



export default function Body() {

    const [jsStatus, setjsStatus] = useState(false)

    const [js, setjs] = useState('0px')

    function animate() {
        setjsStatus(!jsStatus)
        if (jsStatus) {
            //if false and on page load
            setjs('0px')
        } else {
            //if true and on change
            setjs('80%')
        }
    }
    return (
        // <div className="container">
        //     <div className="section01">
        //         <div className='js' onClick={() => { animate() }} style={{ marginLeft: `${jsmargin}` }}>
        //             {/* <h2>{`${jsText}`}</h2> */}
        //             <span className='js1'><h2>{`${jsText}`}</h2></span>
        //         </div>
        //         <div className='js1' style={{ marginLeft: `${js1margin}` }}>
        //             <h2>{`${js1Text}`}</h2>
        //         </div>

        //         <div className='js2' style={{ marginLeft: `${js1margin}` }}>
        //             <h2>{`${js1Text}`}</h2>
        //         </div>
        //     </div>
        //     <div className="section02">this is body</div>
        //     <div className="section03">this is body</div>
        // </div >

        <div class="container">
            <div class="js4" onClick={() => { animate() }} style={{ backgroundColor: "white" }}>
                white
            </div>
            <div class="js3" style={{ backgroundColor: "red" }}>
                red
            </div>
            <div class="js2" style={{ backgroundColor: "green" }}>
                green
            </div>
            <div class="js1" style={{ backgroundColor: "blue" }}>
                blue
            </div>
            <div class="js" onClick={() => { animate() }} style={{ backgroundColor: "purple", marginRight: `${js}` }}>
                <h1>purpule</h1>
            </div>
            {/* <div class="inner hide" style={{ backgroundcolor: "black" }}></div> */}
        </div>
    )
}
