import React, { Component, useState, useEffect } from 'react'
import "./Section02.css"

export default function Section02() {
    // const [marginLeft, setmarginLeft] = useState(0)
    const [fontSize, setfontSize] = useState(null)

    const [Pystatus, setPystatus] = useState(false)

    const [Pytext, setPytext] = useState("Python+")
    const [Pandastext, setPandastext] = useState("")
    const [Numpytext, setNumpytext] = useState("")
    const [Djangotext, setDjangotext] = useState("")
    const [Tkintertext, setTkintertext] = useState("")
    //left
    const [Pymargin, setPymargin] = useState("45%")
    const [Pandasmargin, setPandasmargin] = useState("45%")
    const [Numpymargin, setNumpymargin] = useState("45%")
    const [Djangomargin, setDjangomargin] = useState("45%")
    const [Tkintermargin, setTkintermargin] = useState("45%")
    //top
    const [Pymargintop, setPymargintop] = useState("0%")
    const [Pandasmargintop, setPandasmargintop] = useState("0%")
    const [Numpymargintop, setNumpymargintop] = useState("0%")
    const [Djangomargintop, setDjangomargintop] = useState("0%")
    const [Tkintermargintop, setTkintermargintop] = useState("0%")

    function animatePy() {
        setPystatus(!Pystatus)
        if (Pystatus) {
            //left
            setPymargin("45%")
            setPandasmargin("45%")
            setNumpymargin("45%")
            setDjangomargin("45%")
            setTkintermargin("45%")
            //top
            setPymargintop("0%")
            setPandasmargintop("0%")
            setNumpymargintop("0%")
            setDjangomargintop("0%")
            setTkintermargintop("0%")

            //text
            setPytext("Python+")
            setPandastext("")
            setNumpytext("")
            setDjangotext("")
            setTkintertext("")

        } else {
            //left
            setPymargin("45%")
            setPandasmargin("10%")
            setNumpymargin("25%")
            setDjangomargin("60%")
            setTkintermargin("80%")
            //top
            setPymargintop("0%")
            setPandasmargintop("5%")
            setNumpymargintop("-1%")
            setDjangomargintop("-1%")
            setTkintermargintop("5%")
            //text
            setPytext(" - ")
            setPandastext("Pandas")
            setNumpytext("Numpy")
            setDjangotext("")
            setTkintertext("")
        }
    }

    function setFontsize() {
        // setmarginLeft(window.innerWidth / 5)
        if (window.innerWidth < 960) {
            setfontSize(`${window.innerWidth * (3 / 100)}px`)
        } else {
            setfontSize(`${window.innerWidth * (4 / 100)}px`)
        }
    }
    useEffect(() => {
        setFontsize();
    });
    window.addEventListener('resize', setFontsize);
    return (
        <div className="containerpy">
            <div className="Pandas" style={{ marginTop: `${Pandasmargintop}`, marginLeft: `${Pandasmargin}`, fontSize: `${fontSize}` }}>Pandas</div>
            <div className="Numpy" style={{ marginTop: `${Numpymargintop}`, marginLeft: `${Numpymargin}`, fontSize: `${fontSize}` }}>Numpy</div>
            <div className="Django" style={{ marginTop: `${Djangomargintop}`, marginLeft: `${Djangomargin}`, fontSize: `${fontSize}` }}>Django</div>
            <div className="Tkinter" style={{ marginTop: `${Tkintermargintop}`, marginLeft: `${Tkintermargin}`, fontSize: `${fontSize}` }}>Tkinter</div>
            <div className="Python" onClick={() => { animatePy() }} style={{ marginTop: `${Pymargintop}`, marginLeft: `${Pymargin}`, fontSize: `${fontSize}` }}>{`${Pytext}`}</div>
        </div>
    )
}


