import React, { Component, useState, useEffect } from 'react'
import "../Body/Body.css"



export default function Body() {
    //full screen 1920
    //font size = 2% of screen size
    //window.alert(window.innerWidth)

    const [windowSize, setwindowSize] = useState(0)
    const [marginLeft, setmarginLeft] = useState(0)
    const [fontSize, setfontSize] = useState(null)

    const [jsStatus, setjsStatus] = useState(false)
    const [pyStatus, setpyStatus] = useState(false)

    const [js, setjs] = useState(0)
    const [js1, setjs1] = useState(0)
    const [js2, setjs2] = useState(0)
    const [js3, setjs3] = useState(0)
    const [js4, setjs4] = useState(0)


    const [jsText, setjsText] = useState('JavaScript <-')
    const [js1Text, setjs1Text] = useState('')
    const [js2Text, setjs2Text] = useState('')
    const [js3Text, setjs3Text] = useState('')
    const [js4Text, setjs4Text] = useState('')

    const [pyText, setpyText] = useState('Python <-')
    const [py1Text, setpy1Textt] = useState('')
    const [py2Text, setpy2Text] = useState('')
    const [py3Text, setpy3Text] = useState('')
    const [py4Text, setpy4Text] = useState('')

    function setFontsize() {
        // setwindowSize(window.innerWidth)
        // setmarginLeft(windowSize - (windowSize * (20.5 / 100)))
        setmarginLeft(window.innerWidth / 5)
        if (window.innerWidth < 960) {
            setfontSize(`${window.innerWidth * (3 / 100)}px`)
        } else {
            setfontSize(`${window.innerWidth * (4 / 100)}px`)
        }
    }

    function animatejs() {
        setjsStatus(!jsStatus)
        if (jsStatus) {

            //if false and on page load
            //animation
            setjs(0)
            setjs1(0)
            setjs2(0)
            setjs3(0)
            setjs4(0)
            //Text
            setTimeout(() => {
                setjsText('JavaScript <-')
                setjs1Text('')
                setjs2Text('')
                setjs3Text('')
                setjs4Text('')
            }, 350);

        } else {

            //if true and on change
            //animation
            setjs(marginLeft * 3.9)
            setjs1(marginLeft * 2.3)
            setjs2(marginLeft * 1.015)
            setjs3(marginLeft * -0.5)
            setjs4(marginLeft * -2.2)
            //Text
            // eslint-disable-next-line no-script-url
            setjsText('->')
            setjs1Text('React ')
            setjs2Text('Angular ')
            setjs3Text('Node.JS ')
            setjs4Text('and more')
        }
    }
    function animatepy() {
        setjsStatus(!jsStatus)
        if (jsStatus) {

            //if false and on page load
            //animation
            setjs(0)
            setjs1(0)
            setjs2(0)
            setjs3(0)
            setjs4(0)
            //Text
            setTimeout(() => {
                setjsText('JavaScript <-')
                setjs1Text('')
                setjs2Text('')
                setjs3Text('')
                setjs4Text('')
            }, 350);

        } else {

            //if true and on change
            //animation
            setjs(marginLeft * 3.9)
            setjs1(marginLeft * 2.3)
            setjs2(marginLeft * 1.015)
            setjs3(marginLeft * -0.5)
            setjs4(marginLeft * -2.2)
            //Text
            // eslint-disable-next-line no-script-url
            setjsText('->')
            setjs1Text('React ')
            setjs2Text('Angular ')
            setjs3Text('Node.JS ')
            setjs4Text('and more')
        }
    }

    useEffect(() => {
        setFontsize();
    });
    window.addEventListener('resize', setFontsize);
    return (
        // <div className="container">
        //     <div className="section01">
        //         <div className='js' onClick={() => { animate() }} style={{ marginLeft: `${jsmargin}` }}>
        //             {/* <h2>{`${jsText}`}</h2> */}
        //             <span className='js1'><h2>{`${jsText}`}</h2></span>
        //         </div>
        //         <div className='js1' style={{ marginLeft: `${js1margin}` }}>
        //             <h2>{`${js1Text}`}</h2>
        //         </div>

        //         <div className='js2' style={{ marginLeft: `${js1margin}` }}>
        //             <h2>{`${js1Text}`}</h2>
        //         </div>
        //     </div>
        //     <div className="section02">this is body</div>
        //     <div className="section03">this is body</div>
        // </div >
        <>
            <div className="container1">
                <div className="js4" style={{ marginRight: `${js4}px`, fontSize: `${fontSize}` }}>
                    {`${js4Text}`}
                </div>
                <div className="js3" style={{ marginRight: `${js3}px`, fontSize: `${fontSize}` }}>
                    {`${js3Text}`}
                </div>
                <div className="js2" style={{ marginRight: `${js2}px`, fontSize: `${fontSize}` }}>
                    {`${js2Text}`}
                </div>
                <div className="js1" style={{ marginRight: `${js1}px`, fontSize: `${fontSize}` }}>
                    {`${js1Text}`}
                </div>
                <div className="js" onClick={() => { animatejs() }} style={{ marginRight: `${js}px`, fontSize: `${fontSize}` }}>
                    {`${jsText}`}
                </div>
            </div>

            <div className="container2">
                <div className="js4" style={{ marginRight: `${js4}px`, fontSize: `${fontSize}` }}>
                    {`${js4Text}`}
                </div>
                <div className="js3" style={{ marginRight: `${js3}px`, fontSize: `${fontSize}` }}>
                    {`${js3Text}`}
                </div>
                <div className="js2" style={{ marginRight: `${js2}px`, fontSize: `${fontSize}` }}>
                    {`${js2Text}`}
                </div>
                <div className="js1" style={{ marginRight: `${js1}px`, fontSize: `${fontSize}` }}>
                    {`${js1Text}`}
                </div>
                <div className="js" onClick={() => { animatepy() }} style={{ marginRight: `${js}px`, fontSize: `${fontSize}` }}>
                    {`${jsText}`}
                </div>
            </div>
        </>
    )
}
