import React, { useState, useEffect } from 'react'
import Image from 'react-bootstrap/Image'
import linkedin from "../Resources/1617158814_LinkedIn-logo-PNG-Image.png"
import github from "../Resources/images.png"
import cellphone from "../Resources/cellphone.png"
import "../FooterBar/FooterBar.css"
export default function FooterBar() {
    const [fontSize, setfontSize] = useState(window.innerWidth * (3 / 100))
    function changeFontSize() {
        setfontSize(window.innerWidth * (3 / 100))
    }

    useEffect(() => {
        changeFontSize();
    });

    window.addEventListener('resize', changeFontSize)
    function buttom() {
        return (
            <table className="table">
                <tbody className="tbody">

                    <tr className="tr">
                        <td className="td">
                            <div className="cellphone">
                                <a href="tel:+14163194673" className="cell"><h3>+1 (416) 319 - 4673</h3></a>
                            </div>
                        </td>
                        <td className="td" style={{ fontSize: fontSize }}>Kanta Husari</td>
                    </tr>

                    <tr className="tr">
                        <td className="td">
                            <div className="cellphone">
                                <a href="mailto:kanta.husari@gmail.com" className="cell" style={{ fontSize: fontSize }}>kanta.husari@gmail.com</a>
                            </div>
                        </td>
                        <td className="td">
                            <div className="socialdivlinkedin">
                                <Image src={linkedin} className='socialimagelinkedin' />
                            </div>
                            <div className="socialdivgithub">
                                <Image src={github} className='socialimagegithub' />
                            </div>
                        </td>
                    </tr>
                </tbody>
            </table>
        )
    }
    return (
        < footer className="footer" >
            {buttom()}
        </footer >
    )
}
