import React, { Component, useState, useEffect } from 'react'
import "./Section02.css"

export default function Section02() {
    const [marginLeft, setmarginLeft] = useState(0)
    const [fontSize, setfontSize] = useState(null)


    function setFontsize() {
        // setwindowSize(window.innerWidth)
        // setmarginLeft(windowSize - (windowSize * (20.5 / 100)))
        setmarginLeft(window.innerWidth / 5)
        if (window.innerWidth < 960) {
            setfontSize(`${window.innerWidth * (3 / 100)}px`)
        } else {
            setfontSize(`${window.innerWidth * (4 / 100)}px`)
        }
    }
    useEffect(() => {
        setFontsize();
    });
    window.addEventListener('resize', setFontsize);
    return (
        <div className="containerpy">
            <div className="Pandas" >Pandas</div>
            <div className="Numpy">Numpy</div>
            <div className="Django">Django</div>
            <div className="Tkinter">Tkinter</div>
            {/* <div className="right" style={{ fontSize: `${fontSize}` }}><h1>Python</h1></div> */}
            <div className="Python" style={{ fontSize: `${fontSize}` }}>Python</div>
        </div>
    )
}


