/* eslint-disable jsx-a11y/alt-text */
import React, { useState, useEffect } from 'react'
import "./Skills.css"

import design from "../../Resources/Design.png"
import coding from "../../Resources/data.png"
import frameworks from "../../Resources/framework.png"
import databases from "../../Resources/server.png"
import devops from "../../Resources/DevOps.png"

export default function Skills() {
    const skills = {
        html: {
            name: "HTML",
            percentage: "90%",
        },
        css: {
            name: "CSS",
            percentage: "70%",
        },
        sass: {
            name: "Sass",
            percentage: "50%",
        },
        Bootstrap: {
            name: "Bootstrap",
            percentage: "90%",
        },
        Photoshop: {
            name: "Photoshop",
            percentage: "90%",
        },


        JavaScript: {
            name: "JavaScript",
            percentage: "75%",
        },
        PHP: {
            name: "PHP",
            percentage: "60%",
        },
        Python: {
            name: "Python",
            percentage: "70%",
        },
        CS: {
            name: "C#",
            percentage: "50%",
        },
        Java: {
            name: "Java",
            percentage: "50%",
        },
        MySQL: {
            name: "MySQL",
            percentage: "80%",
        },
        PostgreSQL: {
            name: "PostgreSQL",
            percentage: "50%",
        },
        SQLServer: {
            name: "SQL Server",
            percentage: "45%",
        },
        SQLite: {
            name: "SQLite",
            percentage: "70%",
        },
        MongoDB: {
            name: "MongoDB",
            percentage: "70%",
        },
        GraphQL: {
            name: "GraphQL",
            percentage: "60%",
        },
        NodeJS: {
            name: "Node.JS",
            percentage: "70%",
        },
        Express: {
            name: "Express",
            percentage: "60%",
        },
        Mern: {
            name: "Mern",
            percentage: "75%",
        },
        Mean: {
            name: "Mean",
            percentage: "60%",
        },
        Django: {
            name: "Django",
            percentage: "50%",
        },
        Tkinter: {
            name: "Tkinter",
            percentage: "50%",
        },
        GitHub: {
            name: "GitHub",
            percentage: "60%",
        },
        Docker: {
            name: "Docker",
            percentage: "50%",
        },
        TravisCI: {
            name: "TravisCI",
            percentage: "60%",
        },
        AWS: {
            name: "AWS",
            percentage: "45%",
        },
        DigitalOcean: {
            name: "DigitalOcean",
            percentage: "45%",
        },
    }

    const [title, settitle] = useState("Skills")
    //click status
    const [shhdesign, setshhdesign] = useState(false)
    const [shhcoding, setshhcoding] = useState(false)
    const [shhframeworks, setshhframeworks] = useState(false)
    const [shhdatabases, setshhdatabases] = useState(false)
    const [shhdevops, setshhdevops] = useState(false)

    //show rooms
    const [designshowroom, setdesignshowroom] = useState("none")
    const [codingshowroom, setcodingshowroom] = useState("none")
    const [frameworksshowroom, setframeworksshowroom] = useState("none")
    const [databasesshowroom, setdatabasesshowroom] = useState("none")
    const [devopsshowroom, setdevopsshowroom] = useState("none")

    // show hide functions
    function showDesign() {
        setshhdesign(!shhdesign)
        //set all other false
        if (shhdesign) {
            //hide
            settitle("Skills")
            //others all none
            setdesignshowroom("none")
            setcodingshowroom("none")
            setframeworksshowroom("none")
            setdatabasesshowroom("none")
            setdevopsshowroom("none")

        } else {
            //show
            settitle("Design")
            setdesignshowroom("block")
            //others all none
            setcodingshowroom("none")
            setframeworksshowroom("none")
            setdatabasesshowroom("none")
            setdevopsshowroom("none")

        }
    }

    function showCoding() {
        setshhcoding(!shhcoding)
        if (shhcoding) {
            //hide
            settitle("Skills")
            //all none
            setdesignshowroom("none")
            setcodingshowroom("none")
            setframeworksshowroom("none")
            setdatabasesshowroom("none")
            setdevopsshowroom("none")

        } else {
            //show
            settitle("Coding")
            //others all none
            setdesignshowroom("none")
            setcodingshowroom("block")
            setframeworksshowroom("none")
            setdatabasesshowroom("none")
            setdevopsshowroom("none")

        }
    }

    function showFramework() {
        setshhframeworks(!shhframeworks)
        if (shhframeworks) {
            //hide
            settitle("Skills")
            setdesignshowroom("none")
            //others all none
            setcodingshowroom("none")
            setframeworksshowroom("none")
            setdatabasesshowroom("none")
            setdevopsshowroom("none")

        } else {
            //show
            settitle("Design")
            setdesignshowroom("block")
            //others all none
            setcodingshowroom("none")
            setframeworksshowroom("none")
            setdatabasesshowroom("none")
