import React, { Component, useState, useEffect } from 'react'
import "../Body/Body.css"



export default function Body() {
    //full screen 1920
    //font size = 2% of screen size
    //window.alert(window.innerWidth)

    const [windowSize, setwindowSize] = useState(window.innerWidth)
    const [fontSize, setfontSize] = useState(null)

    const [jsStatus, setjsStatus] = useState(false)

    const [js, setjs] = useState('0px')
    const [js1, setjs1] = useState('0px')
    const [js2, setjs2] = useState('0px')
    const [js3, setjs3] = useState('0px')
    const [js4, setjs4] = useState('0px')

    function setFontsize() {
        // setwindowSize(window.innerWidth)
        // const screenSize = window.innerWidth
        setwindowSize(window.innerWidth)
        if (windowSize < 960) {
            setfontSize(`${windowSize * (3 / 100)}px`)
        } else {
            setfontSize(`${windowSize * (4 / 100)}px`)
        }
    }

    function animate() {
        console.log(window.innerWidth)
        setjsStatus(!jsStatus)
        if (jsStatus) {
            //if false and on page load
            //animation
            setjs('0px')
            setjs1('0px')
            setjs2('0px')
            setjs3('0px')
            setjs4('0px')
        } else {
            //if true and on change
            //animation
            setjs(`${windowSize - 100}px`)
            setjs1('72%')
            setjs2('60%')
            setjs3('50%')
            setjs4('40%')

        }
    }
    useEffect(() => {
        setFontsize();
    });
    window.addEventListener('resize', setFontsize);
    return (
        // <div className="container">
        //     <div className="section01">
        //         <div className='js' onClick={() => { animate() }} style={{ marginLeft: `${jsmargin}` }}>
        //             {/* <h2>{`${jsText}`}</h2> */}
        //             <span className='js1'><h2>{`${jsText}`}</h2></span>
        //         </div>
        //         <div className='js1' style={{ marginLeft: `${js1margin}` }}>
        //             <h2>{`${js1Text}`}</h2>
        //         </div>

        //         <div className='js2' style={{ marginLeft: `${js1margin}` }}>
        //             <h2>{`${js1Text}`}</h2>
        //         </div>
        //     </div>
        //     <div className="section02">this is body</div>
        //     <div className="section03">this is body</div>
        // </div >

        <div className="container">
            <div className="js4" style={{ backgroundColor: "white", marginRight: `${js4}`, fontSize: `${fontSize}` }}>
                and more
            </div>
            <div className="js3" style={{ backgroundColor: "red", marginRight: `${js3}`, fontSize: `${fontSize}` }}>
                Node.JS
            </div>
            <div className="js2" style={{ backgroundColor: "green", marginRight: `${js2}`, fontSize: `${fontSize}` }}>
                Angular
            </div>
            <div className="js1" style={{ backgroundColor: "blue", marginRight: `${js1}`, fontSize: `${fontSize}` }}>
                React
            </div>
            <div className="js" onClick={() => { animate() }} style={{ backgroundColor: "purple", marginRight: `${js}`, fontSize: `${fontSize}` }}>
                JavaScript
            </div>
            {/* <div className="inner hide" style={{ backgroundcolor: "black" }}></div> */}
        </div>
    )
}
